  _formatInput(event) {
    event.target.value = event.target.value
      .replace(/[^\dA-Z]/g, "")
      .replace(/(\d)(?=(\d{3})+$)/g, "$1 ")
      .trim();
  }

  getAnswers() {
    this._answers = [];
    this._questionElements.forEach((question) =>
      this._answers.push(question.selectedIndex - 1)
    );

    // this.helpRequestString = this.supportLevelElement.options[
    //   this.supportLevelElement.selectedIndexR
    // ].text;

    // this.helpRequestTicked = this.helpCheckboxElement.checked;
    // console.log(this.helpRequestTicked);
  }

  getInvestmentAmount() {
    this._getCurrency();

    this.investmentAmount =
      +[...this.inputElements][0].value.replace(/\s/g, "") >=
      10000 * this.prices[this.currency]
        ? +[...this.inputElements][0].value.replace(/\s/g, "")
        : 10000 * this.prices[this.currency];

    this.investmentAmountRubbles =
      this.investmentAmount / this.prices[this.currency];
  }

  updatePrices(prices) {
    this.prices = {
      dollar: prices["USD"],
      // euro: prices["EUR"],
      ruble: 1,
    };
  }

  // getFilter() {
  //   this.filterList = [...this._filterElement.querySelectorAll("label")]
  //     .filter((element) => {
  //       return element.querySelector("input").checked;
  //     })
  //     .map((element) => element.innerText);
  // }

  getClientId() {
    this.clientId = this.clientIdInputElement.value;
  }

  assignRiskProfile() {
    this.riskProfiles = [
      this._answers[2] + 1,
      this._answers[2] + 1 < 5 ? this._answers[2] + 2 : this._answers[2] + 1,
    ];

    // console.log(this.riskProfiles);
  }

  assignPortfolioKeys() {
    const letters = "abcdefg";

    this.portfolioKeys = this.riskProfiles.map(
      (profile) => profile + letters[this._answers[0]]
    );

    // console.log(this.portfolioKeys);
  }

  assignDueDate() {
    switch (this._answers[3]) {
      case 0:
        this.dueDate = new Date(
          new Date().setFullYear(new Date().getFullYear() + 1)
        );
        break;
      case 1:
        this.dueDate = new Date(
          new Date().setFullYear(new Date().getFullYear() + 2)
        );
        break;
      case 2:
      default:
        this.dueDate = new Date(
          new Date().setFullYear(new Date().getFullYear() + 100)
        );
    }
  }

  _getCurrency() {
    switch (this._answers[1]) {
      case 0:
        this.currency = "ruble";
        this.isCurrency = false;
        break;
      case 1:
        this.currency = "dollar";
        this.isCurrency = true;
        break;
      // case 2:
      //   this.currency = "euro";
      //   this.isCurrency = true;
      //   break;
    }
  }

